sub vcl_hash {
    hash_data(req.url);

    # Begin SSL load balancer config
    if (req.url ~ "www.mydomain.url" &amp;&amp; req.http.X-Forwarded-For) {
        hash_data(req.http.X-Forwarded-For);
    }
    # End SSL load balancer config

    if (req.http.host) {
        hash_data(req.http.host);
    } else {
        hash_data(server.ip);
    }

    return (lookup);
}

